function getNow(){
    var d = new Date();
    var n = d.getTime();
    return n;    
}
var timeoutdelayseconds =15;
var now = getNow();
msg.payload.fun = "";

//take care of globals so we can work with locals
    lTable = global.get("table");

    if (lTable === undefined){
        msg.payload.fun += "undefined ";
        lTable = [];
        for (var i=0;i<255;i=i+1){
            lTable[i] = {"position":i,"mac":"default","timeout":now};
        }
        global.set("table", lTable);
    }else{
        msg.payload.fun += "defined ";
    }

var lTable = global.get("table");

for (var i=0;i<255;i=i+1){
    if (lTable[i].mac == msg.payload.register){
        var use =i;
        msg.payload.fun += "already registered ";
        break;
    }
    if (lTable[i].timeout <= getNow()){
        use = i;
        break;
    }

}

lTable[use].timeout = getNow()+(1000*timeoutdelayseconds);
lTable[use].mac = msg.payload.register;

global.set("table", lTable);

msg.payload.table = lTable;
return msg;

lTable = global.get("table");
  if (lTable === undefined){
     msg.payload.fun = "undefined"; 
